package samplePrograms;

public class ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
					// TODO Auto-generated method stub
				int numberOne = 100;
				int numberTwo = 2;

			 System.out.println("numberOne + numberTwo =" + (numberOne + numberTwo));
			 System.out.println("numberOne - numberTwo =" + (numberOne - numberTwo));
			 System.out.println("numberOne * numberTwo =" + (numberOne * numberTwo));
			 System.out.println("numberOne / numberTwo =" + (numberOne / numberTwo));
			 System.out.println("numberOne % numberTwo =" + (numberOne % numberTwo));
			 
			     }
		}

