package samplePrograms;

public class IncrementDecrement {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 10;
		int value = ++i;
		int value1 = i++;
		int value2 = i--;
		int value3 = --i;
		
		System.out.println("++I values is = " + value);
		System.out.println("++I values is = " + value1);
		System.out.println("I-- values is = " + value2);
		System.out.println("--I values is = " + value3);

	}

}

